          ...payload,
        }
      ]

      file.write(EVNTBOARD_CACHE_FILENAME, JSON.stringify(newData,  0, 2))

      newEvent({
        idParent: event.id,
        name: 'evntboard-patch',
        payload: newData,
        emitter
      })
      break;
    }
    case "evntboard-update": {
      let data = JSON.parse(file.read(EVNTBOARD_CACHE_FILENAME))

      const newData = [
        ...data.filter((i) => i.id !== payload.id),
        {
          ...EVNTBOARD_SAMPLE_BOARD, // au cas ou !
          ...payload
        }
      ]

      file.write(EVNTBOARD_CACHE_FILENAME, JSON.stringify(newData,  0, 2))

      newEvent({
        idParent: event.id,
        name: 'evntboard-patch',
        payload: newData,
        emitter
      })
      break;
    }
    case "evntboard-delete": {
      let data = JSON.parse(file.read(EVNTBOARD_CACHE_FILENAME))

      const newData = data.filter((i) => i.id !== payload.id)

      file.write(EVNTBOARD_CACHE_FILENAME, JSON.stringify(newData,  0, 2))

      newEvent({
        idParent: event.id,
        name: 'evntboard-patch',
        payload: newData,
        emitter
      })
      break;
    }
    case "evntboard-tmp": {
      let evntboardCache = temp.get("evntboard-cache")
      if (!evntboardCache) {
        evntboardCache = []
        evntboardCache.push(payload)
      } else {
        evntboardCache = [
          ...evntboardCache.filter((i) => i.slug !== payload.slug ),
          {
            ...evntboardCache.find((i) => i.slug === payload.slug),
            ...payload
          }
        ]
      }
      temp.set("evntboard-cache", evntboardCache)
      newEvent({
        idParent: event.id,
        name: 'evntboard-patch-tmp',
        payload: evntboardCache,
        emitter
      })
    }
